import os
import unittest
import spacy
from nltk import Tree

# model = 'en_core_web_md'
model = 'en_core_web_sm'

nlp = spacy.load(model)

package_directory = os.path.dirname(os.path.abspath(__file__))
path_to_data = package_directory + "/../../../../../../tasks/02-structural-linguistics/data/"


from english_collocations.collocations.collocations import synonyms_to_speak


def takeSecond(elem):
    return elem[1]

def to_nltk_tree(node):
    if node.n_lefts + node.n_rights > 0:
        return Tree(node.orth_, [to_nltk_tree(child) for child in node.children])
    else:
        return node.orth_


class CollocationsTestCases(unittest.TestCase):

    def test_get_synonyms_and_collocations(self):

        with open(path_to_data + 'blog2008.txt') as text_file:
            data = text_file.readlines()

        synonyms = synonyms_to_speak()
        print(synonyms)
        synonyms_set = {'spill', 'recite', 'sing', 'state', 'peach', 'let_the_cat_out_of_the_bag', 'communicate',
                        'blab', 'talk', 'verbalize', 'transmit', 'recount', 'lecture', 'utter', 'evidence', 'say',
                        'pass', 'speak', 'put_across', 'spill_the_beans', 'tattle', 'verbalise', 'narrate', 'blab_out',
                        'pass_on', 'convey', 'babble', 'babble_out', 'claim', 'pass_along', 'tell', 'mouth'}
        doc = nlp(
            "Right then and there , with perfect timing , she began reciting the beautiful verses from \" High Flight \" by Pilot Officer John Gillespie loudly enough for us to hear .")

        for sent in doc.sents:
            tree = to_nltk_tree(sent.root)
            tree.pretty_print()

        synonyms_dictionary = {}

        total_sentences = len(data)

        current_line = 0

        for line in data:
            current_line += 1
            if current_line %100 == 0:
                print (total_sentences, current_line)

            doc = nlp(line)
            for sent in doc.sents:
                for token in sent:
                    if token.lemma_ in synonyms_set:
                        #print(token)
                        for descendant in token.subtree:
                            if descendant.tag_ == "RB" and descendant.text.endswith("ly"):
                                #print(token.lemma_, descendant.text)

                                if token.lemma_ not in synonyms_dictionary:
                                    synonyms_dictionary[token.lemma_] = {}

                                if descendant.lemma_ not in synonyms_dictionary[token.lemma_]:
                                    synonyms_dictionary[token.lemma_][descendant.lemma_] = 0

                                synonyms_dictionary[token.lemma_][descendant.lemma_] += 1

                                #print (synonyms_dictionary)

        print (synonyms_dictionary)



